using UnityEngine;using UnityEngine.AI;namespace NPC{	public class NPCMove : NPCBehavior	{		public NavMeshAgent agent;		private VisibleTargets visibleTargets;		private Vector3 targetPos;		public NPCMove(NavMeshAgent agent, VisibleTargets visibleTargets)		{			this.agent = agent;			this.visibleTargets = visibleTargets;		}		public void SetTarget(Vector3 targetPos)		{			this.targetPos = targetPos;		}		public override void Act(Transform npc, Transform target)		{			agent.SetDestination(targetPos);		}		public override void Reason(Transform npc, Transform target, NPCController controller)		{			if (visibleTargets.closestTarget != null)			{				controller.SetTarget(visibleTargets.closestTarget);				controller.SetTransition(Transition.SawTarget);			}		}	}}